import React from 'react'

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

function Search( {setTop10, top10} ) {
  const resultsRef = React.useRef(null);

  function getResults(){
    if (resultsRef.current.value != ""){
      if (resultsRef.current.value.length == 4){
        if (top10.includes(resultsRef.current.value) == false){

          if (top10.length <= 3){
            setTop10( arr => [resultsRef.current.value, ...arr]);
            //resultsRef.current.value = "";
          }

          if (top10.length == 4){
            setTop10( arr => [resultsRef.current.value, ...arr.splice(-1)]);
            //resultsRef.current.value = "";
          }
          
        }
      }
    }
  }

  return (
    <Form className=''>
    <Row className="">

    <Col md='6' lg='12'>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Search Stock</Form.Label>
        <Form.Control type="text" placeholder="Enter stock" maxLength="4" ref={resultsRef} />
      </Form.Group>
    </Col>

    <Col md='6' lg='12' className="d-flex justify-content-lg-start justify-content-md-end align-items-center">
      <div className='text-start mt-md-3 mt-0 mt-lg-0'>
      <Button variant="outline-primary" onClick = {getResults}>
        Search
      </Button>
      </div>
    </Col>

    </Row>
    </Form>
  );
}

export default Search;
