import React from 'react'

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function Search( {setTop10, top10} ) {
  const resultsRef = React.useRef(null);

  function getResults(){
    if (top10.length <= 9){
      //if(top10[top10.length-1] != resultsRef.current.value){
     if(top10.includes(resultsRef.current.value) == false){
        setTop10( arr => [...arr, resultsRef.current.value]);
      }
    }
  }

  return (
    <Form className=''>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Search Stock</Form.Label>
        <Form.Control type="text" placeholder="Enter stock" pattern=".{}" ref={resultsRef} />
      </Form.Group>

      <div className='text-start'>
      <Button variant="outline-primary" onClick = {getResults}>
        Search
      </Button>
      </div>

    </Form>
  );
}

export default Search;
