import React, { useState } from "react"

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

function Search( {setTop10, top10, setFinalSearch} ) {
  const resultsRef = React.useRef(null);
  const [currentSearch, setCurrentSearch] = useState("");

  const searchResults = [
  "AAPL1", "AAPL2", "AAPL3", "AAPL4", "AAPL5", "AAPL6", "AAPL7", "AAPL8", "AAPL9", "AAPL10","AAPL11", "AAPL12",
  "MSFT1", "MSFT2","MSFT3","MSFT4","MSFT5","MSFT6","MSFT7","MSFT8","MSFT9","MSFT10","MSFT11","MSFT12",
  "GOOG1","GOOG2","GOOG3","GOOG4","GOOG5","GOOG6","GOOG7","GOOG8","GOOG9","GOOG10","GOOG11","GOOG12"
  ]

  function getResults(){
    let temp = resultsRef.current.value.toUpperCase;

    if (temp !== ""){
      if (temp.length >= 4){
        if (top10.includes(temp) === false){

          if (top10.length <= 4){
            setTop10( top10 => [temp, ...top10]);
          }

          if (top10.length >= 5){
            top10.pop()
            setTop10( top10 => [temp, ...top10]);
          }

        }
        setFinalSearch(temp);
      }
    }

    resultsRef.current.value = "";
  }

  function setCurrent(){
    setCurrentSearch(resultsRef.current.value);
  }

  function setResults(item) {
    //resultsRef.current.value = item;
    //setCurrentSearch(resultsRef.current.value);
    resultsRef.current.value = "";
    setCurrentSearch("");
    setFinalSearch(item);

    if (top10.length <= 4){
      setTop10( top10 => [item, ...top10]);
    }

    if (top10.length >= 5){
      top10.pop()
      setTop10( top10 => [item, ...top10]);
    }

  }

  return (
    <Form className='' onSubmit={e => { e.preventDefault(); getResults();}}>
      <Form.Label>Search Stock</Form.Label>
      <InputGroup className="mb-3" controlid="formBasicEmail">
        <Form.Control type="text" placeholder="Enter stock" maxLength="4" ref={resultsRef} onChange = {setCurrent} style={{textTransform: "uppercase"}}/>
        <Button variant="outline-primary" onClick = {() => {getResults();}}>
          Search
        </Button>
      </InputGroup>

      <div> {/*style={{ backgroundColor: "white", display: "flex", flexDirection: "column", border: "1px solid gray"}}*/}
          {searchResults
            .filter((item) => {
              const searchTerm = currentSearch.toLowerCase();
              const fullName = item.toLowerCase();
              console.log(searchTerm, "|", fullName);

              return (
                searchTerm &&
                fullName.startsWith(searchTerm) &&
                fullName !== searchTerm
              );
            })
            .slice(0, 5)
            .map((item) => (
              <div onClick={() => {setResults(item);}} className="dropdown-row" key={item}>
                {item}
              </div>
            ))}
        </div>

    </Form>
  );
}

export default Search;
