import React from 'react'

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function Histroy( {top10, setCurrentSearch} ) {

  function change(){
    setCurrentSearch("Chicken")
  }

  return (
    <Form className='text-center'>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>History</Form.Label>
      </Form.Group>

      <div className='text-center'>
      {/* Every time a search is entered a button should appear here with the results*/}
      {top10.map( (item) =>
          <Button key = {item} variant="outline-primary" className="m-1" onClick={change(item)}>{item}</Button>
        )}
      </div>

    </Form>
  );
}

export default Histroy;
