import React from 'react'

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Inner from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

function Search( {setTop10, top10, setCurrentSearch} ) {
  const resultsRef = React.useRef(null);

  function getResults(){
    let temp = resultsRef.current.value;

    if (temp !== ""){
      if (temp.length === 4){
        if (top10.includes(temp) === false){

          if (top10.length <= 4){
            setTop10( top10 => [temp, ...top10]);
          }

          if (top10.length >= 5){
            top10.pop()
            setTop10( top10 => [temp, ...top10]);
          }

        }
        setCurrentSearch(temp);
      }
    }

    resultsRef.current.value = "";
  }

  function searchResults(){
    const searchResults = [
    "AAPL1", "AAPL2", "AAPL3", "AAPL4", "AAPL5", "AAPL6", "AAPL7", "AAPL8", "AAPL9", "AAPL10","AAPL11", "AAPL12",
    "MSFT1", "MSFT2","MSFT3","MSFT4","MSFT5","MSFT6","MSFT7","MSFT8","MSFT9","MSFT10","MSFT11","MSFT12",
    "GOOG1","GOOG2","GOOG3","GOOG4","GOOG5","GOOG6","GOOG7","GOOG8","GOOG9","GOOG10","GOOG11","GOOG12"
    ]
  }

  return (
    <Form className='' onSubmit={e => { e.preventDefault(); getResults();}}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Search Stock</Form.Label>
        <Form.Control type="text" placeholder="Enter stock" maxLength="4" ref={resultsRef} />
        <div className='text-start mt-md-3 mt-0 mt-lg-0'>
        <Button variant="outline-primary" onClick = {() => {getResults();}}>
          Search
        </Button>
        </div>
      </Form.Group>

      <InputGroup className="mb-3">
       <Form.Control
         placeholder="Recipient's username"
         aria-label="Recipient's username"
         aria-describedby="basic-addon2"
       />
       <Button variant="outline-secondary" id="button-addon2">
         Button
       </Button>
     </InputGroup>

    </Form>
  );
}

export default Search;
