import React from 'react'

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

function Search( {setTop10, top10, setCurrentSearch} ) {
  const resultsRef = React.useRef(null);

  function getResults(){
    let temp = resultsRef.current.value;

    if (temp !== ""){
      if (temp.length === 4){
        if (top10.includes(temp) === false){

          if (top10.length <= 4){
            setTop10( top10 => [temp, ...top10]);
          }

          if (top10.length >= 5){
            top10.pop()
            setTop10( top10 => [temp, ...top10]);
          }

        }
        setCurrentSearch(temp);
      }
    }

    resultsRef.current.value = "";
  }

  function searchResults(){

  }

  return (
    <Form className='' onSubmit={e => { e.preventDefault(); getResults();}}>
      <Form.Label>Search Stock</Form.Label>
      <InputGroup className="mb-3" controlId="formBasicEmail">
        <Form.Control type="text" placeholder="Enter stock" maxLength="4" ref={resultsRef} />
        <Button variant="outline-primary" onClick = {() => {getResults();}}>
          Search
        </Button>
      </InputGroup>
      <div className="dropdown">
          {data
            .filter((item) => {
              const searchTerm = value.toLowerCase();
              const fullName = item.full_name.toLowerCase();

              return (
                searchTerm &&
                fullName.startsWith(searchTerm) &&
                fullName !== searchTerm
              );
            })
            .slice(0, 10)
            .map((item) => (
              <div
                onClick={() => onSearch(item.full_name)}
                className="dropdown-row"
                key={item.full_name}
              >
                {item.full_name}
              </div>
            ))}
        </div>
    </Form>
  );
}

export default Search;
