import React from 'react'

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

function Search( {setTop10, top10, setCurrentSearch} ) {
  const resultsRef = React.useRef(null);

  function getResults(){
    let temp = resultsRef.current.value;

    if (temp != ""){
      if (temp.length == 4){
        if (top10.includes(temp) == false){

          if (top10.length <= 3){
            setTop10( top10 => [temp, ...top10]);
          }

          if (top10.length >= 4){
            top10.pop()
            setTop10( top10 => [temp, ...top10]);
          }

        }
      }
      
    }

    resultsRef.current.value = "";
  }

  return (
    <Form className='' onSubmit={e => { e.preventDefault(); getResults();}}>
    <Row className="">

    <Col md='6' lg='12'>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Search Stock</Form.Label>
        <Form.Control type="text" placeholder="Enter stock" maxLength="4" ref={resultsRef} />
      </Form.Group>
    </Col>

    <Col md='6' lg='12' className="d-flex justify-content-lg-start justify-content-md-end align-items-center">
      <div className='text-start mt-md-3 mt-0 mt-lg-0'>
      <Button variant="outline-primary" onClick = {() => {getResults();}}>
        Search
      </Button>
      </div>
    </Col>

    </Row>
    </Form>
  );
}

export default Search;
