    item = item.toUpperCase();
    resultsRef.current.value = "";
    setCurrentSearch("");
    setFinalSearch(item);

    if (top10.includes(item) === false){

      if (top10.length <= 4){
        setTop10( top10 => [item, ...top10]);
      }

      if (top10.length >= 5){
        top10.pop()
        setTop10( top10 => [item, ...top10]);
      }

    }

  }

